export function createState(inParentComponent: unknown) {

  return {
    currentView: "login",

    showRegisterView: function (): void {
      this.setState({ currentView: "register", name: "", email: "", password: "", passwordConfirmation: "" });

    }.bind(inParentComponent),

    showLoginView: function (): void {
      this.setState({ currentView: "login", email: "", password: "", token: "" });

    }.bind(inParentComponent),

    showHomeView: function (): void {
      this.setState({ currentView: "home" });

    }.bind(inParentComponent),

    handleChange : function(event: any): void {
      event.preventDefault();
      const { name, value } = event.target;
      let errors = this.state.errors;
      switch (name) {
        case 'email':
          errors.email = Regex.test(value) ? '' : 'Email is not valid!';
          break;
        case 'password':
          errors.password = value.length < 8 ? '' : 'Password too long!';
          break;
        default:
          break;
      }
      this.setState(Object.assign(this.state, { errors, [name]: value }));
      console.log(this.state.errors);
    }.bind(inParentComponent),

    fieldChangeHandler : function(inEvent: any): void {

      console.log("state.fieldChangeHandler()", inEvent.target.id, inEvent.target.value);

      // Enforce max length for contact name.
      if (inEvent.target.id === "contactName" && inEvent.target.value.length > 16) { return; }

      this.setState({ [inEvent.target.id] : inEvent.target.value });

    }.bind(inParentComponent), /* End fieldChangeHandler(). */
  };

}
