import axios from "axios";
import React from "react";
var md5 = require("md5");

const Regex = RegExp(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);

let erroName = '';
let erroPass = '';
let erroPassConfirmation = '';
let erroEmail = '';

const RegisterView = ({ state }: any) => {

    const validation = (val: any) => {
        if (val === "201")
            state.showLoginView();
    }

    const handleSubmit = (event: any) => {
        event.preventDefault();
        let validity = true;
        if (erroEmail !== '' && erroPass !== '')
            validity = false;
        if (validity === true) {
            const options = {
                url: 'http://localhost:3000/api/login',
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json;charset=UTF-8'
                },
                data: {
                    name: state.target.name,
                    email: state.email,
                    password: state.password,
                }
            };
            axios(options).then(response => {
                console.log(response.status);
                validation(response.status)
            });

            console.log("Registering can be done");
        } else {
            console.log("You cannot be registered!!!")
        }
    }

    const testPass = (event: any) => {
        erroPass = event.target.value.length < 8 ? '' : 'Password too long!';
        console.log(erroPass)
        return erroPass;
    }

    const testPassConfirmation = (event: any) => {
        erroPassConfirmation = (state.password === event.target.value) ? '' : 'Password are not equal';
        console.log(erroPassConfirmation + " " + event.target.password)
        return erroPassConfirmation;
    }

    const testName = (event: any) => {
        erroName = event.target.value.length > 1 ? '' : 'Name too short!';
        console.log(erroName)
        return erroName;
    }

    const testEmail = (event: any) => {
        erroEmail = Regex.test(event.target.value) ? '' : 'Email is not valid!';
        console.log(erroEmail)
        return erroEmail;
    }

    return (
        <div className="App">
        <header className="App-header">
          <div className="wrap-login">
            <div className="login-form-title">
              Register
            </div>
            <form onSubmit={handleSubmit} noValidate>
              <div className="black">
                Name<br />
                <input type="text" name="name" onChange={testName} /><br />
                {<span style={{ color: "red" }}>{erroName}</span>}
              </div>
              <div className="black">
                Email<br />
                <input type="text" name="email" onChange={testEmail} /><br />
                {<span style={{ color: "red" }}>{erroEmail}</span>}
              </div>
              <div className="black">
                Password<br />
                <input type="password" name="password" onChange={testPass} /><br />
                {<span style={{ color: "red" }}>{erroPass}</span>}
              </div>
              <div className="black">
                Password Confirmation<br />
                <input type="password" name="passwordConfirmation" onChange={testPassConfirmation} /><br />
                {<span style={{ color: "red" }}>{erroPassConfirmation}</span>}
              </div>
              <input type="submit" value="Log in" />
              <br />
            </form>
          </div>
        </header>
      </div>
    );
}

export default RegisterView;
