import React from "react";

interface LoginUpProps {
  name?: any;
  value?: any;
}
interface LoginUpState {
  email: string,
  password: string,
  errors: {
    email: string,
    password: string
  }
}

const Regex = RegExp(/^\s?[A-Z0–9]+[A-Z0–9._+-]{0,}@[A-Z0–9._+-]+\.[A-Z0–9]{2,4}\s?$/i);

export class LoginView extends React.Component<LoginUpProps, LoginUpState> {
  handleChange = (event: any) => {
    event.preventDefault();
    const { name, value } = event.target;
    this.setState({ [name]: value });
    console.log(this.state);
  }
  handleSubmit = (event: any) => { }

  constructor(props: LoginUpProps) {
    super(props);
    const initialState = {
      email: '',
      password: '',
      errors: {
        email: '',
        password: ''
      }
    }
    this.state = initialState;
    this.handleChange = this.handleChange.bind(this);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div className="wrap-login">
            <div className="login-form-title">
              Login
            </div>
            <form onSubmit={this.handleSubmit} noValidate>
              <div className="black">
                Email<br />
                <input type="text" name="email" onChange={this.handleChange} />
              </div>

              <div className="black">
                Password<br />
                <input type="password" name="pass" onChange={this.handleChange} />
              </div>
              <input type="submit" value="Log in" />
              <br />
              {/* <button onClick={state.showRegisterView} > */}
              {/* Register */}
              {/* </button> */}
            </form>
          </div>
        </header>
      </div>
    );
  }
}
