              <div className="black">
                Name<br />
                <input type="text" name="name" onChange={testName} /><br />
                {errors.name.length > 0 && <span style={{ color: "red" }}>{errors.name}</span>}
              </div>
              <div className="black">
                Email<br />
                <input type="text" name="email" onChange={testEmail} /><br />
                {errors.email.length > 0 && <span style={{ color: "red" }}>{errors.email}</span>}
              </div>
              <div className="black">
                Password<br />
                <input type="password" name="password" onChange={testPass} /><br />
                {errors.password.length > 0 && <span style={{ color: "red" }}>{errors.password}</span>}
              </div>
              <div className="black">
                Password Confirmation<br />
                <input type="password" name="passwordConfirmation" onChange={testPassConfirmation} /><br />
                {errors.passwordConfirmation.length > 0 && <span style={{ color: "red" }}>{errors.passwordConfirmation}</span>}
              </div>
              <input type="submit" value="Log in" />
              <br />
            </form>
          </div>
        </header>
      </div>
    );
}

export default RegisterView;
