import React from "react";

interface LoginUpProps {
  name?: any;
  value?: any;
}
interface LoginUpState {
  email: string,
  password: string,
  errors: {
    email: string,
    password: string
  }
}

const Regex = RegExp(/^\s?[A-Z0–9]+[A-Z0–9._+-]{0,}@[A-Z0–9._+-]+\.[A-Z0–9]{2,4}\s?$/i);

export class LoginView extends React.Component<LoginUpProps, LoginUpState> {
  handleChange = (event: any) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;
    switch (name) {
      case 'email':
        // errors.email = Regex.test(value) ? '' : 'Email is not valid!';
        break;
      case 'password':
        errors.password = value.length < 8 ? 'Password must be eight characters long!' : '';
        break;
      default:
        break;
    }
    this.setState(Object.assign(this.state, { errors, [name]: value }));
    console.log(this.state.errors);
  }
  
  handleSubmit = (event : any) => {
    event.preventDefault();
    let validity = true;
    Object.values(this.state.errors).forEach(
      (val) => val.length > 0 && (validity = false)
    );
    if(validity == true){
       console.log("Registering can be done");
    }else{
       console.log("You cannot be registered!!!")
    }
 }

  constructor(props: LoginUpProps) {
    super(props);
    const initialState = {
      email: '',
      password: '',
      errors: {
        email: '',
        password: ''
      }
    }
    this.state = initialState;
    this.handleChange = this.handleChange.bind(this);
  }

  render() {
    const {errors} = this.state
    return (
      <div className="App">
        <header className="App-header">
          <div className="wrap-login">
            <div className="login-form-title">
              Login
            </div>
            <form onSubmit={this.handleSubmit} noValidate>
              <div className="black">
                Email<br />
                <input type="text" name="email" onChange={this.handleChange} /><br/>
                {errors.email.length > 0 &&  <span style={{color: "red"}}>{errors.email}</span>}
              </div>
              <div className="black">
                Password<br />
                <input type="password" name="passowrd" onChange={this.handleChange} /><br/>
                {errors.password.length > 0 &&  <span style={{color: "red"}}>{errors.password}</span>}
              </div>
              <input type="submit" value="Log in" />
              <br />
              {/* <button onClick={state.showRegisterView} > */}
              {/* Register */}
              {/* </button> */}
            </form>
          </div>
        </header>
      </div>
    );
  }
}
