import React from "react";

interface LoginUpProps {
  name?: any;
  value?: any;
}
interface LoginUpState {
  name: string,
  email: string,
  password: string,
  passwordConfirmation: string,
  errors: {
    name: string,
    email: string,
    password: string,
    passwordConfirmation: string,
  }
}

const Regex = RegExp(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);

export class LoginView extends React.Component<LoginUpProps, LoginUpState> {
  handleChange = (event: any) => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;
    switch (name) {
      case 'name':
        errors.name = value.length > 1 ? '' : 'Name too short!';
        break;
      case 'email':
        errors.email = Regex.test(value) ? '' : 'Email is not valid!';
        break;
      case 'password':
        errors.password = value.length < 8 ? '' : 'Password too long!';
        break;
      case 'passwordConfirmation':
        errors.passwordConfirmation = (this.state.password == value) ? '' : 'Password are not equal';
        break;
      default:
        break;
    }
    this.setState(Object.assign(this.state, { errors, [name]: value }));
    console.log(this.state.errors);
  }

  handleSubmit = (event: any) => {
    event.preventDefault();
    let validity = true;
    Object.values(this.state.errors).forEach(
      (val) => val.length > 0 && (validity = false)
    );
    if (validity == true) {
      console.log("Registering can be done");
    } else {
      console.log("You cannot be registered!!!")
    }
  }

  constructor(props: LoginUpProps) {
    super(props);
    const initialState = {
      name: '',
      email: '',
      password: '',
      passwordConfirmation: '',
      errors: {
        name: '',
        email: '',
        password: '',
        passwordConfirmation: ''
      }
    }
    this.state = initialState;
    this.handleChange = this.handleChange.bind(this);
  }

  render() {
    const { errors } = this.state
    return (
      <div className="App">
        <header className="App-header">
          <div className="wrap-login">
            <div className="login-form-title">
              Register
            </div>
            <form onSubmit={this.handleSubmit} noValidate>
              <div className="black">
                Name<br />
                <input type="text" name="name" onChange={this.handleChange} /><br />
                {errors.name.length > 0 && <span style={{ color: "red" }}>{errors.name}</span>}
              </div>
              <div className="black">
                Email<br />
                <input type="text" name="email" onChange={this.handleChange} /><br />
                {errors.email.length > 0 && <span style={{ color: "red" }}>{errors.email}</span>}
              </div>
              <div className="black">
                Password<br />
                <input type="password" name="password" onChange={this.handleChange} /><br />
                {errors.password.length > 0 && <span style={{ color: "red" }}>{errors.password}</span>}
              </div>
              <div className="black">
                Password Confirmation<br />
                <input type="password" name="passwordConfirmation" onChange={this.handleChange} /><br />
                {errors.passwordConfirmation.length > 0 && <span style={{ color: "red" }}>{errors.passwordConfirmation}</span>}
              </div>
              <input type="submit" value="Log in" />
              <br />
              {/* <button onClick={state.showRegisterView} > */}
              {/* Register */}
              {/* </button> */}
            </form>
          </div>
        </header>
      </div>
    );
  }
}
